let inputs=document.querySelectorAll('label[invalid] > *[interactive="true"]');for(let i=0;i<inputs.length;i++)inputs[i].addEventListener("input",validate);function validate(){let a=this.closest("label"),b=a.querySelector(".form-group__error");if(""===this.value||("checkbox"===this.type||"radio"===this.type)&&!this.checked){a.setAttribute("invalid",""),b.style.display="block";return}a.removeAttribute("invalid"),b.style.display="none"}let selects=document.querySelectorAll("select");for(let i=0;i<selects.length;i++)selects[i].addEventListener("input",updateValue);let dates=document.querySelectorAll('input[type^="date"]');for(let i=0;i<dates.length;i++)dates[i].addEventListener("input",updateValue);let times=document.querySelectorAll('input[type="time"]');for(let i=0;i<times.length;i++)times[i].addEventListener("input",updateValue);function updateValue(){this.setAttribute("value",this.value)}let textareas=document.querySelectorAll('textarea[autoexpand="true"]');for(let i=0;i<textareas.length;i++)textareas[i].addEventListener("input",resize);function resize(){this.style.height="auto",this.style.height=this.scrollHeight+4+"px"}
